mapper.select:
select ort.rn, ort.no, to_char(ort.buyDate, 'yy.mm.dd') buyDate, ort.usePlace, ort.point, ort.name cateName, ort.status from (select rownum rn, ot.no, ot.buyDate, ot.usePlace, ot.point, ot.name, ot.status from (select cu.no no, cu.buyDate, cu.usePlace, cu.point point, ca.name, cu.status from category ca, brand br, coupon co, couponUsage cu, users u where u.no = cu.userno and ca.no = br.cateNo and br.no = co.brandno and co.no = cu.couponNo and u.no = #{userNo}
and #{endDate} >= cu.buyDate-1
and cu.buyDate >= #{startDate}
and status = #{option2}
and ca.name = #{option1}
order by cu.no desc )ot )ort where rn>=#{startRnum} and rn<=#{endRnum}

		
			select count(*) count
			from category ca, brand br, coupon co, couponUsage cu, users u
			where u.no = cu.userno
			and ca.no = br.cateNo
			and br.no = co.brandno
			and co.no = cu.couponNo
			and u.no = #{userNo}
		

		
			and #{endDate} >= cu.buyDate-1
		

		
			and cu.buyDate >= #{startDate}
		

		
			and status = #{option2}
		

		
			and ca.name = #{option1}

		
			select co.no couponNo,
                   imgsrc
			from category ca, brand br, coupon co
			where  ca.no = br.cateNo
			and br.no = co.brandno
		

		
			and #{maxValue} >= point
		

		
			and point >= #{minValue}
		

		
			and ca.name = #{option1}
		

		
			and br.name = #{option2}

select ort.rn, ort.no, ort.type, to_char(ort.regDate, 'yy.mm.dd') regDate, ort.splace, ort.eplace, ort.point from (select rownum rn, ot.no, ot.type, ot.regdate, ot.splace, ot.eplace, ot.point from (select no, type, regDate, splace, eplace, point from (select u.no no, sp.type, pu.regDate, pu.type ptype, se.splace, se.eplace, pu.point from spotmate sp, reservation re, pointUsage pu, users u, (select sno, smateno, sday, splace, eno, emateno, eday, eplace from (select p.no sNo, mp.mateNo sMateNo, p.day sDay, p.place sPlace from place p, (select mateNo, min(day) minDay from place where wayNo = -1 group by mateNo) mp where p.mateno = mp.mateno and p.day = mp.minDay and p.wayNo= -1) s,
(select p.no eNo, mp.mateNo eMateNo, p.day eDay, p.place ePlace from place p, (select mateNo, max(day) maxDay from place where wayNo = 0 group by mateNo) mp where p.mateno = mp.mateno and p.day = mp.maxDay and p.wayNo =0) e where s.sMateNo = e.eMateNo)se where sp.no = se.sMateNo and re.mateno = sp.no and pu.reno = re.no and u.no = pu.userno and u.no = #{userNo} and pu.type= '완료'
and sp.type = #{option1}
union
select userno no, type, regdate, null ptype, null splace, null eplace, point from pointUsage where userno = #{userNo} and (type = '충전' or type='환불' or type='구매')
and type = #{option1}
and #{endDate} >= regDate-1
and regDate >= #{startDate}
)order by regDate desc )ot )ort where rn>=#{startRnum} and rn<=#{endRnum}

		
			select count(*) count
			from pointUsage p, users u
			where p.userNo = u.no
			and u.no= #{userNo}
		

		
			and #{endDate} >= regDate-1
		

		
			and regDate >= #{startDate}
		

		
			and p.type = #{option1}

		
			select sum(point)
			from pointUsage
			where userno = #{userNo}

		
			select  ort.rn,
			        to_char(ort.regDate, 'yy.mm.dd') regDate,
			        ort.accountNum,
			        ort.status,
			        ort.point
			    from (select  rownum rn,
			                  ot.regDate,
			                  ot.accountNum,
			                  ot.status,
			                  ot.point
			            from (select  regDate,
								      accountNum,
								      status,
								      point
						      from pointrefund pr, users u
							  where pr.userNo = u.no
							  and u.no = #{userNo}

		

		
			and #{endDate} >= regDate-1
		

		
			and regDate >= #{startDate}
		

		
			and status = #{option1}
		
	
		
				 order by regDate desc
				 )ot
			    )ort
			where rn>=#{startRnum}
			and rn<=#{endRnum}

		
			select count(*) count
			from pointrefund pr, users u
			where pr.userNo = u.no
			and u.no = #{userNo}
		

		
			and #{endDate} >= status
		

		
			and regDate >= #{startDate}
		

		
			and status = #{option1}
