mapper.select:
select people
from reservation
where mateNo = #{mateNo}

select latlng
from place
where mateNo = #{mateNo}

mapper.update:
update reservation
set 
	people = #{canRide}

where mateNo = #{mateNo}

update place
set 
	latlng = #{lng}||','||#{lat}||','||#{addr}
where mateNo = #{mateNo}
and wayno = 0
