mapper.select:
select introduce
from car
where no = #{userNo}

select latlng
from place
where mateNo = #{mateNo}

mapper.update:

update reservation 

set 
	regDate = sysdate,
	rideUser = #{lat}||','||#{lng}

	where mateNo = #{mateNo}


update spotmate 
set people = people - #{people}

where no = #{mateNo}

mapper.insert:


		select
			people
		from reservation
		where mateNo = #{mateNo}
		and userNo = #{userNo}


update spotmate
set
	people = people + #{no}
where no = #{mateNo}

mapper.delete:
delete from
reservation
where mateNo = #{mateNo}
and userNo = #{userNo}

mapper.update:
update spotmate
set
people = #{canRide}

where no = #{mateNo}

update place
set
latlng = #{lng}||','||#{lat}||','||#{addr}
where mateNo = #{mateNo}
and wayno
= 0
